import React, { useState, useEffect } from "react";

export default function Select(props) {
  const [options, setOptions] = useState([]);
  const [vaa,setVaa]=useState('')
  // const [option,setOption]=useState(['SELECT',...options])

  useEffect(() => {
    // props.onChange(props.id, "");
    const fetchList = async () => {
      const resp = await fetch(
        `api/faculty${props.url}/`
      );
      const data = await resp.json();
      setOptions(data);
    };
    fetchList();
  }, [props.url,props.reset]);
  console.log("nithiesh",options);

// useEffect(()=>{
//   setOption(["SELECT",...options])
// },[options])

  const changeHandler = (e) => {
// console.log(e)
    // setVaa(e.target.value)
    // if(e.target.value==='SELECT')props.onChange(props.id, "")
  // else if(props.reset===1){
  //   props.onChange(props.id,e.target.value="")
  //   // setVaa("")
  // }
// else
props.onChange(props.id, e.target.value)};
// console.log("vanakkam",vaa)
useEffect(()=>{
  if(props.reset==1){
    setOptions(options)
  }
},[props.reset])


  return (
    <div className="search-input-groups">
      <label>{props.label}</label>
      <select onClick={(e)=>changeHandler(e)
      // setVaa(e.target.value)
      
      // value=e.target.value
    }
      >
        {options.length > 0 &&
          options.map((x) => (
            <option  key={x}>
              {x}
            </option>
          ))}
      </select>
    </div>
  );
}
