import React, { useState, useEffect } from "react";
import axios from "axios";
import Input from "./Input";
import ResultsList from "./Results/ResultsList";
import MultiRangeSlider from "./MultiRangeSlider";
import Select from "./Select";
export default function SearchContainer(props) {
  const [query, setQuery] = useState({});
  const [reset,setReset]=useState(0);
  const [List, setList] = useState([]);
  const [PersonData, setPersonData] = useState([]);
  const [searchID, setSearchID] = useState("");
  const [valuesList, setValuesList] = useState({});
  const [Index, setIndex] = useState({
    start: 0,
    end: 18,
  });
  // console.log = console.warn = console.error = () => {};
  // Updating State when input values change
  const changeHandler = (key, val) => {
    // console.log("key and val" ,key, val)
    // console.log("Test", valuesList[key].min.current)
    // setIndex({
    //   start: 0,
    //   end: 18,
    // });
    // if (Object.keys(query).length <= 0) return;
    Object.keys(valuesList).indexOf(key) >= 0 ? 
    setQuery((prevState)=>({
      ...prevState,
      [key]: {min: valuesList[key].min.current, max: valuesList[key].max.current}
    }))
    :
    setQuery((prevState) => ({
      ...prevState,
      [key]: val,

    }));
    
  };

  // fetching list of faculties

  useEffect(() => {
    const response = async () => {
      const { data } = await axios.get(
        `api/faculty/lists/`
      );
      JSON.stringify(data);
      setList(data);
      setPersonData(data);
    };
    response();
  }, []);

  // Displaying Faculty Details by their Id

  useEffect(() => {
    if (searchID === "") return;
    // console.log(searchID);
    const fetchPerson = async () => {
      const response = fetch(
        `api/faculty/lists/?faculty_id__icontains=${searchID}`
      );
      const person = await (await response).json();
      // console.log("faculty", person);
      setPersonData(person);
    };
    fetchPerson();
  }, [searchID]);
  // console.log(PersonData,'nnk')

  // request based on Input values

  useEffect(() => {
    let url = "api/faculty/lists/?";
    // console.log("QQQQQQQuery", query)
    if (!Object.keys(query)) return;
    Object.keys(query).forEach((x) => {
      url +=
        Object.keys(valuesList).indexOf(x) >=0
          ? `${x}__lte=${valuesList[x].max.current}&${x}__gte=${valuesList[x].min.current}&`
          : `${x}__icontains=${query[x]}&`;
    });
    const fetchData = async (url) => {
      const resp = await fetch(`/${url}`);
      const response = await resp.json();
      setPersonData(response);
      setList(response);
      
    };
    fetchData(url);
    // console.warn("urlll",url)
    // console.warn("urlll",query)
  }, [query,valuesList]);
  console.log("vaa",query)
  useEffect(()=>{
    setReset(0)
    // console.log("Queryyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyyy",query)
  }, [query])

  useEffect(()=>{
  }, [valuesList])

  useEffect(()=>{
    if(reset==1){

    setQuery({})}
  },[reset])
  return (
    <>
      <div className="search-container">
        {facultyData.map((field,index) =>
          field["data-element"] === "input" ? (
           field['type'] === 'range' ?   <MultiRangeSlider key={index} reset={reset}
           {...field} list = {setValuesList}
           onChange={changeHandler}
         /> : <Input  key={index}  reset={reset} {...field} onChange={changeHandler} />
          ) : (
            // <Input label={field.label} type={field.type} />
            <Select key={index} reset={reset} {...field} onChange={changeHandler} />
          )
        )}
      </div>
      

      <button onClick={(e)=>
        // window.location.reload(false)
        setReset(1)
        // setQuery({})
        }>Refresh Page</button>
      <div className="search-results-container">
        <hr />
        <div className="search-results">
          
          {/* checking whether the states have same length before rendering */}
          {PersonData.length === List.length || PersonData.length > 0 ? (
            <>
              <ResultsList
                list={List}
                updateId={setSearchID}
                updateIndex={setIndex}
                Index={Index}
              />
              
            </>
          ) : (
            <>
              {/* This element will render when there is no match found by checking the length of state variable ie, PersonData */}
              <span>No Match Found</span>
            </>
          )}
        </div>
      </div>
    </>
  );
}

const facultyData = [
  {
    label: "Faculty Id",
    type: "text",
    "data-element": "input",
    id: "faculty_id",
  },
  {
    label: "Faculty Name",
    type: "text",
    "data-element": "input",
    id: "name",
  },
  {
    label: "Designation",
    "data-element": "select",
    id: "designation",
    url: "/designation",
  },
  {
    label: "Department",
    "data-element": "select",
    id: "department",
    url: "/dept",
  },
  {
    label: "Central Responsibility",
    "data-element": "select",
    id: "central_responsibility",
    url: "/cr",
  },
  {
    label: "Email Id",
    type: "text",
    "data-element": "input",
    id: "email",
  },
  // {
  //   label: "Date of joining",
  //   type: "date",
  //   "data-element": "input",
  //   id: "date_of_joining",
  // },
  {
    label: "FAP 2021 Score",
    type: "range",
    min: 0,
    max: 1,
    step: 0.1,
    "data-element": "input",
    id: "FAP_2021_Score",
  },
  {
    label: "Feedback 2021 Score",
    type: "range",
    min: 0,
    max: 4,
    step: 0.1,
    "data-element": "input",
    id: "Feedback_2021_Score",
  },
  {
    label: "FRP 2021",
    type: "range",
    min: 0,
    max: 1,
    step: 0.1,
    "data-element": "input",
    id: "FRP_2021",
  },
  {
    label: "FRS 2021",
    type: "range",
    min: -5000,
    max: 5000,
    step: 25,
    "data-element": "input",
    id: "FRS_2021",
  },
];
